import React, { useState, useEffect } from 'react';
import { render } from 'react-dom';
import { EditorState, convertToRaw, convertFromRaw, RichUtils, AtomicBlockUtils } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import './App.css';
import 'draft-js/dist/Draft.css';
import Button from './components/Button'; // Assuming Button component

const App = () => {
  const [editorState, setEditorState] = useState(EditorState.createEmpty());
  const [useTextArea, setUseTextArea] = useState(true); // Flag to control editor mode

  useEffect(() => {
    const savedContent = localStorage.getItem('draftContent');
    if (savedContent) {
      const contentState = convertFromRaw(JSON.parse(savedContent));
      setEditorState(EditorState.createWithContent(contentState));
      setUseTextArea(false); // Switch to Draft.js editor if saved content exists
    }
  }, []); // Empty dependency array to run only once on mount

  const handleInputChange = (e) => {
    // Handle input change logic for textarea (if applicable)
  };

  const handleKeyPress = (e) => {
    // Handle keypress formatting logic for textarea (if applicable)
  };

  const onChange = (newEditorState) => {
    setEditorState(newEditorState);
  };

  const saveContent = () => {
    const contentState = editorState.getCurrentContent();
    const rawContentState = convertToRaw(contentState);
    localStorage.setItem('draftContent', JSON.stringify(rawContentState));
  };

  const handleKeyDown = (e) => {
    // Handle keydown logic for formatting within Draft.js editor
  };

  const handleKeyCommand = (command, editorState) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  };

  const createCodeBlock = () => {
    // Handle code block creation within Draft.js editor
  };

  const toggleEditorMode = () => {
    setUseTextArea(!useTextArea); // Toggle between textarea and Draft.js editor
    if (useTextArea) {
      // Convert Draft.js content to plain text for textarea on switching
    } else {
      // Convert plain text from textarea to Draft.js content on switching
    }
  };

  return (
    <div>
      <div className="header-container">
        <h2>DraftJs Editor</h2>
        <Button label={useTextArea ? 'Switch to Draft Editor' : 'Switch to Text Area'} onClick={toggleEditorMode} />
        <Button label="Save" onClick={saveContent} />
      </div>
      {useTextArea ? (
        <textarea
          value={false}
          onChange={handleInputChange}
          onKeyDown={handleKeyPress}
          placeholder="Start typing..."
          style={{ width: '100%', height: '300px' }}
        />
      ) : (
        <Editor
          editorState={editorState}
          onEditorStateChange={onChange}
          handleKeyCommand={handleKeyCommand}
          onKeyDown={handleKeyDown}
        />
      )}
    </div>
  );
};

render(<App />, document.getElementById('root'));
