/* eslint-disable react/jsx-no-undef */
import React, { Component } from 'react';
import { render } from 'react-dom';
import { EditorState, convertToRaw, convertFromRaw, Rich } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import './App.css';
import Button from './components/Button';

class EditorContainer extends Component {
  constructor(props) {
    super(props);
    const savedContent = localStorage.getItem('draftContent');
    if (savedContent) {
      const contentState = convertFromRaw(JSON.parse(savedContent));
      this.state = { editorState: EditorState.createWithContent(contentState) };
    } else {
      this.state = { editorState: EditorState.createEmpty() };
    }
  }

  onChange = (editorState) => {
    this.setState({ editorState });
  };

  handleKeyCommand = (command, editorState) => {
    let newState = EditorState.forceSelection(editorState, editorState.getSelection());
    switch (command) {
      case 'apply-heading':
        newState = RichUtils.toggleBlockType(editorState, 'header-one');
        break;
      case 'apply-bold':
        newState = RichUtils.toggleInlineStyle(editorState, 'BOLD');
        break;
      case 'apply-red-line':
        // Implement red line formatting
        break;
      case 'apply-underline':
        // Implement underline formatting
        break;
      case 'apply-code-block':
        // Implement code block formatting
        break;
      default:
        newState = RichUtils.handleKeyCommand(editorState, command);
        break;
    }
    if (newState) {
      this.onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  };

  keyBindingFn = (e) => {
    if (e.keyCode === 32) { // Spacebar
      const { editorState } = this.state;
      const contentState = editorState.getCurrentContent();
      const selection = editorState.getSelection();
      const block = contentState.getBlockForKey(selection.getStartKey());
      const blockText = block.getText();
      if (blockText.startsWith('# ')) {
        return 'apply-heading';
      } else if (blockText.startsWith('* ')) {
        return 'apply-bold';
      } else if (blockText.startsWith('** ')) {
        return 'apply-red-line';
      } else if (blockText.startsWith('*** ')) {
        return 'apply-underline';
      } else if (blockText.startsWith('````` ')) {
        return 'apply-code-block';
      }
    }
    return null;
  };

  saveContent = () => {
    const { editorState } = this.state;
    const contentState = editorState.getCurrentContent();
    const rawContentState = convertToRaw(contentState);
    localStorage.setItem('draftContent', JSON.stringify(rawContentState));
  };

  render() {
    const { editorState } = this.state;
    return (
      <div>
        <Editor
          editorState={editorState}
          onChange={this.onChange}
          handleKeyCommand={this.handleKeyCommand}
          keyBindingFn={this.keyBindingFn}
        />
        <Button label="Save" onClick={this.saveContent} />
      </div>
    );
  }
}

const App = () => (
  <div>
    <h2>DraftJs Editor by Uday Mahale</h2>
    <EditorContainer />
  </div>
);

render(<App />, document.getElementById('root'));
