import React, { Component } from 'react';
import { render } from 'react-dom';
import { EditorState, convertToRaw, convertFromRaw, RichUtils } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import './App.css';
import Button from './components/Button';

class App extends Component {
  constructor(props) {
    super(props);
    const savedContent = localStorage.getItem('draftContent');
    if (savedContent) {
      const contentState = convertFromRaw(JSON.parse(savedContent));
      this.state = { editorState: EditorState.createWithContent(contentState) };
    } else {
      this.state = { editorState: EditorState.createEmpty() };
    }
    this.onChange = this.onChange.bind(this);
    this.saveContent = this.saveContent.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.handleKeyCommand = this.handleKeyCommand.bind(this); // Add this line
  }

  onChange(editorState) {
    this.setState({ editorState });
  }

  saveContent() {
    const contentState = this.state.editorState.getCurrentContent();
    const rawContentState = convertToRaw(contentState);
    localStorage.setItem('draftContent', JSON.stringify(rawContentState));
  }

  handleKeyDown(e) {
    if (e.key === '#' && e.target.tagName.toLowerCase() === 'div') {
      // Prevent default '#' character insertion
      e.preventDefault();
      // Convert the '#' character to a "Header One" format
      this.onChange(RichUtils.toggleBlockType(this.state.editorState, 'header-one'));
    } else if (e.key === ' ' && e.target.tagName.toLowerCase() === 'div') {
      const editorState = this.state.editorState;
      const selection = editorState.getSelection();
      const currentContent = editorState.getCurrentContent();
      const block = currentContent.getBlockForKey(selection.getStartKey());
      const blockType = block.getType();

      // Check if the current block type is "header-one"
      if (blockType === 'header-one') {
        // If the current block is "header-one" and space is pressed, remain in "header-one" format
        return 'handled';
      }
    }
  }

  handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      this.onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  }

  render() {
    const { editorState } = this.state;
    return (
      <div>
        <div className="header-container">
          <h2>DraftJs Editor by Uday Mahale</h2>
          <Button label="Save" onClick={this.saveContent} />
        </div>
        <Editor
          editorState={editorState}
          onEditorStateChange={this.onChange}
          handleKeyCommand={this.handleKeyCommand} // Add handleKeyCommand event handler
          onKeyDown={this.handleKeyDown} // Add keydown event handler
        />
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));
