import React, { useState, useRef } from 'react';
import { EditorState, RichUtils, At } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import Button from './components/Button'; // Assuming Button component

function App() {
  const [editorState, setEditorState] = useState(() => EditorState.createEmpty());
  const editorRef = useRef(null);

  const handleChange = (newEditorState) => {
    setEditorState(newEditorState);
  };

  const handleKeyPress = (event) => {
    if (event.key === '#' && !isPreviousCharHashtag()) {
      event.preventDefault();
      setEditorState(RichUtils.toggleBlockType(editorState, 'header-one'));
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === ' ' && isPreviousCharHashtag()) {
      event.preventDefault();
      removeHashtag();
    }
  };

  const isPreviousCharHashtag = () => {
    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const node = range.startContainer;
    const text = node.textContent.substring(0, range.startOffset);
    return text.endsWith('#');
  };

  const removeHashtag = () => {
    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const node = range.startContainer;
    const text = node.textContent;
    const updatedText = text.substring(0, range.startOffset - 1) + text.substring(range.startOffset);
    node.textContent = updatedText;
    range.setStart(node, range.startOffset - 1);
    selection.removeAllRanges();
    selection.addRange(range);
  };

  const saveContent = () => {
    const contentState = editorState.getCurrentContent();
    const rawContentState = convertToRaw(contentState);
    localStorage.setItem('draftContent', JSON.stringify(rawContentState));
  };

  const handleKeyCommand = (command) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState);
      return 'handled';
    }
    return 'not-handled';
  };

  const createCodeBlock = () => {
    const codeBlock = AtomicBlockUtils.createBlock('atomic');
    const newState = AtomicBlockUtils.insertAtomicBlock(editorState, codeBlock);
    setEditorState(newState);
  };

  return (
    <div>
      <div className="header-container">
        <h2>DraftJs Editor by Uday Mahale</h2>
        <Button label="Save" onClick={saveContent} />
      </div>
      <Editor
        editorState={editorState}
        onEditorStateChange={handleChange}
        handleKeyCommand={handleKeyCommand}
        onKeyDown={handleKeyDown}
        onKeyPress={handleKeyPress}
        ref={editorRef}
      />
    </div>
  );
}

export default App;
