import React, { Component } from 'react';
import { render } from 'react-dom';
import './App.css';
import 'draft-js/dist/Draft.css';
import Button from './components/Button'; // Assuming Button component
import { useState } from 'react'; // Import useState hook

const TextEditor = () => {
  const [text, setText] = useState('');

  const handleInputChange = (e) => {
    const newText = e.target.value;
    setText(newText);
  };

  const handleKeyPress = (e) => {
    const { key } = e;
    let newText = text;

    // Check if the first character is #
    if (key === ' ' && newText.trim().startsWith('# ')) {
      newText = newText.replace(/^# /, '');
      setText(`# ${newText}`);
      e.preventDefault(); // Prevents the space from being inserted
    }
    // Check if the first character is *
    else if (key === ' ' && newText.trim().startsWith('* ')) {
      newText = newText.replace(/^\* /, '');
      setText(`* ${newText}`);
      e.preventDefault(); // Prevents the space from being inserted
    }
    // Check if the first characters are **
    else if (key === ' ' && newText.trim().startsWith('** ')) {
      newText = newText.replace(/^\*\* /, '');
      setText(`** ${newText}`);
      e.preventDefault(); // Prevents the space from being inserted
    }
    // Check if the first characters are ***
    else if (key === ' ' && newText.trim().startsWith('*** ')) {
      newText = newText.replace(/^\*\*\* /, '');
      setText(`*** ${newText}`);
      e.preventDefault(); // Prevents the space from being inserted
    }

    // Check if the user is typing ```
    else if (newText.endsWith('``` ')) {
      setText(`${newText}\n\n\`\`\``);
      e.preventDefault(); // Prevents the space from being inserted
    }
  };

  return (
    <textarea
      value={text}
      onChange={handleInputChange}
      onKeyDown={handleKeyPress}
      placeholder="Start typing..."
      style={{ width: '100%', height: '300px' }}
    />
  );
};

class App extends Component {
  constructor(props) {
    super(props);
    const savedContent = localStorage.getItem('draftContent');
    if (savedContent) {
      const contentState = convertFromRaw(JSON.parse(savedContent));
      this.state = { editorState: EditorState.createWithContent(contentState) };
    } else {
      this.state = { editorState: EditorState.createEmpty() };
    }
    this.onChange = this.onChange.bind(this);
    this.saveContent = this.saveContent.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.handleKeyCommand = this.handleKeyCommand.bind(this);
  }

  onChange(editorState) {
    this.setState({ editorState });
  }

  saveContent() {
    const contentState = this.state.editorState.getCurrentContent();
    const rawContentState = convertToRaw(contentState);
    localStorage.setItem('draftContent', JSON.stringify(rawContentState));
  }

  handleKeyDown(e) {
    const { editorState } = this.state;
    const selection = editorState.getSelection();

    if (e.key === '#' && e.target.tagName.toLowerCase() === 'div') {
      e.preventDefault();
      this.onChange(RichUtils.toggleBlockType(editorState, 'header-one'));
    } else if (e.key === '*' && e.target.tagName.toLowerCase() === 'div') {
      if (selection.getAnchorOffset() === 0) {
        e.preventDefault();
        this.onChange(RichUtils.toggleInlineStyle(editorState, 'BOLD'));
      }
    } else if (e.key === '*' && e.target.tagName.toLowerCase() === 'div') {
      if (selection.getAnchorOffset() === 0) {
        e.preventDefault();
        if (e.shiftKey) {
          this.onChange(RichUtils.toggleInlineStyle(editorState, 'RED'));
        } else {
          this.onChange(RichUtils.toggleInlineStyle(editorState, 'UNDERLINE'));
        }
      }
    } else if (e.key === '`' && e.target.tagName.toLowerCase() === 'div') {
      if (e.shiftKey) {
        e.preventDefault();
        this.createCodeBlock();
      }
    }
  }

  handleKeyCommand(command, editorState) {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      this.onChange(newState);
      return 'handled';
    }
    return 'not-handled';
  }

  createCodeBlock() {
    const codeBlock = AtomicBlockUtils.createBlock('atomic');
    const newState = AtomicBlockUtils.insertAtomicBlock(this.state.editorState, codeBlock);
    this.onChange(newState);
  }

  render() {
    const { editorState } = this.state;
    return (
      <div>
        <div className="header-container">
          <h2>DraftJs Editor by Uday Mahale</h2>
          <Button label="Save" onClick={this.saveContent} />
        </div>
        <TextEditor /> {/* Use the TextEditor component instead of Editor */}
      </div>
    );
  }
}

render(<App />, document.getElementById('root'));
